import React, { useState, useRef, useEffect } from "react";
import { ContentsInput, TitleInput } from "./styledComponent";

function InputPost({onChange, contents, title}) {
  return (
    <>
      <TitleInput
        name="title"
        type="text"
        placeholder="제목을 입력해주세요. (15자 이내)"
        value={title}
        onChange={onChange}
      />
      <ContentsInput
        name="contents"
        value={contents}
        cols="30"
        rows="10"
        onChange={onChange}
      ></ContentsInput>
    </>
  );
}

export default InputPost;
