import { Main, MediaDiv } from "./styledComponent";

import { darkTheme, GlobalStyles, lightTheme } from "./styles";
import { ThemeProvider } from "styled-components";
import { useState } from "react";
import Header from "./Header";
import Slogun from "./Slogun";
import ShowPostList from "./ShowPostList";
import Footer from "./Footer";
import { Routes, Route } from "react-router-dom";
import ShowPost from "./ShowPost";
import WritePost from "./WritePost";

function App() {
  const initialPostList = [
    { id: 1, title: "Hello World!", replCount: 1 },
    { id: 2, title: "Hello World!", replCount: 43 },
    { id: 3, title: "Hello World!", replCount: 12 },
  ];
  const [darkMode, setDarkMode] = useState(true);
  const [loading, setLoading] = useState(false);
  const [isPost, setIsPost] = useState(false);
  const [postList, setPostList] = useState(initialPostList);

  const addPost = () => {
    setPostList((postList) => [
      ...postList,
      { id: 4, title: "Hello World!", replCount: 21 },
    ]);
  };

  return (
    <>
      <ThemeProvider theme={darkMode ? darkTheme : lightTheme}>
        <MediaDiv>
          <Header darkMode={darkMode} setDarkMode={setDarkMode} />
          <Main>
            <Slogun></Slogun>
            <Routes>
              <Route
                path="/"
                element={
                  <ShowPostList
                    loading={loading}
                    isPost={isPost}
                    postList={postList}
                    addPost={addPost}
                  />
                }
              ></Route>
              <Route path="/write" element={<WritePost></WritePost>}></Route>
              <Route
                path="/post/:postID"
                element={<ShowPost></ShowPost>}
              ></Route>
            </Routes>
          </Main>
          <Footer />
        </MediaDiv>
      </ThemeProvider>
    </>
  );
}

export default App;
