import React from "react";
import { PostTitleDiv, ReplSubmitDiv, ReplTitleDiv, WriterDiv } from "./styledComponent";

const ShowPost = () => {
  const [post, setPost] = useState(postData);
  const [repls, setRepls] = useState(replData);
  const [postLoading, setPostLoading] = useState(true);
  const [replLoading, setReplLoading] = useState(false);

  const [repl, setRepl] = useState("");
  const replCount = 0;
  return (
    <div>
      <PostSection>
        <PostTitleDiv>
          <PostTitle>{post && posttilte}</PostTitle>
        </PostTitleDiv>

        {postLoading ? (
          <LoadingDiv>
            <LoadingImg src={`${process.env.PUBLIC_URL}/img/loading.svg`} />
          </LoadingDiv>
        ) : (
          <PostReplDiv>{post && post.contents}</PostReplDiv>
        )}
        {/*post contents*/}
        <ReplTitleDiv>댓글 {replCount} </ReplTitleDiv>
        {replLoading ? (
            <LoadingDiv>
                <LoadingImg src={`${process.env.PUBLIC_URL}/img/loading.svg`}/>
            </LoadingDiv>
        ) :(
            repls && repls.map((element) => (
                <PostReplDiv key={element.id}>
                    <ReplWriter>익명</ReplWriter>
                    <Repl>{element.contents}</Repl>
                </PostReplDiv>
            ))
        )}
        <WriterDiv>
            <ReplInput></ReplInput>
            <ReplSubmitDiv>
                <span>입력</span>
            </ReplSubmitDiv>
        </WriterDiv>
      </PostSection>
    </div>
  );
};

export default ShowPost;
